import React from 'react';
import { Task } from '../types';
import { motion } from 'framer-motion';

interface TaskItemProps {
  task: Task;
  onDelete: (id: string) => void;
  onEdit: (task: Task) => void;
}

const TaskItem: React.FC<TaskItemProps> = ({ task, onDelete, onEdit }) => {
  const handleDeleteCheckbox = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.stopPropagation(); // evitar abrir ediciÃ³n
    // confirmaciÃ³n opcional
    if (window.confirm(`Â¿Eliminar la tarea "${task.title}"?`)) {
      onDelete(task.id);
    } else {
      e.currentTarget.checked = false; // desmarcar checkbox si cancelÃ³
    }
  };

  return (
    <motion.li
      className="task-item"
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.9 }}
      transition={{ duration: 0.3 }}
      onClick={() => onEdit(task)}
      style={{ display: 'flex', alignItems: 'center', gap: 12, cursor: 'pointer' }}
    >
      <input
        type="checkbox"
        onChange={handleDeleteCheckbox}
        aria-label={`Eliminar tarea ${task.title}`}
      />
      <div style={{ flexGrow: 1 }}>
        <h3 style={{ margin: 0 }}>{task.title}</h3>
        <p style={{ margin: '4px 0' }}>{task.description}</p>
        <p style={{ margin: '2px 0', fontSize: '0.9em', color: '#666' }}>
          ðŸ“… {new Date(task.dueDate).toLocaleDateString()} | âš¡ {task.priority} | âœ… {task.status}
        </p>
      </div>
    </motion.li>
  );
};

export default TaskItem;
