import React from 'react';
import { Task } from '../types';

interface TaskItemProps {
    id: string;
    title: string;
    description: string;
    dueDate: string;
    priority: string;
    status: string;
    onDelete: (id: string) => void;
    onUpdate: (id: string, updatedTask: Partial<Omit<TaskItemProps, 'id'>>) => void;
}

const TaskItem: React.FC<TaskItemProps> = ({ id, title, description, dueDate, priority, status, onDelete, onUpdate }) => {
    const handleDelete = () => {
        onDelete(id);
    };

    const handleUpdate = () => {
        const updatedTask = { title, description, dueDate, priority, status }; // Aquí podrías implementar un formulario para editar
        onUpdate(id, updatedTask);
    };

    return (
        <div className="task-item">
            <h3>{title}</h3>
            <p>{description}</p>
            <p>Fecha de vencimiento: {dueDate}</p>
            <p>Prioridad: {priority}</p>
            <p>Estado: {status}</p>
            <button onClick={handleUpdate}>Actualizar</button>
            <button onClick={handleDelete}>Eliminar</button>
        </div>
    );
};

export default TaskItem;
