import React, { useState, useEffect } from 'react';
import { Task } from '../types';
import { v4 as uuidv4 } from 'uuid';

interface TaskFormProps {
  addTask: (task: Task) => Promise<void>;
  updateTask: (task: Task) => Promise<void>;
  task: Task | null;
  cancel: () => void;
}

const TaskForm: React.FC<TaskFormProps> = ({ addTask, updateTask, task, cancel }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [priority, setPriority] = useState('medium');
  const [status, setStatus] = useState('pending');

  // Cuando cambia la tarea a editar, precarga campos
  useEffect(() => {
    if (task) {
      setTitle(task.title);
      setDescription(task.description);
      setDueDate(task.dueDate ? new Date(task.dueDate).toISOString().substr(0, 10) : '');
      setPriority(task.priority);
      setStatus(task.status);
    } else {
      // limpiar para nueva tarea
      setTitle('');
      setDescription('');
      setDueDate('');
      setPriority('medium');
      setStatus('pending');
    }
  }, [task]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const taskToSave: Task = {
      id: task ? task.id : uuidv4(),
      title,
      description,
      dueDate: new Date(dueDate),
      priority: priority.toLowerCase() as 'low' | 'medium' | 'high',
      status: status.toLowerCase() as 'pending' | 'completed',
    };

    if (task) {
      await updateTask(taskToSave);
    } else {
      await addTask(taskToSave);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: '2rem' }}>
      <input
        type="text"
        placeholder="Título"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <input
        type="text"
        placeholder="Descripción"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <input
        type="date"
        value={dueDate}
        onChange={(e) => setDueDate(e.target.value)}
        required
      />
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option value="low">Baja</option>
        <option value="medium">Media</option>
        <option value="high">Alta</option>
      </select>
      <select value={status} onChange={(e) => setStatus(e.target.value)}>
        <option value="pending">Pendiente</option>
        <option value="completed">Completada</option>
      </select>
      <div style={{ display: 'flex', gap: '12px', marginTop: '12px' }}>
        <button type="submit">{task ? 'Actualizar tarea' : 'Agregar tarea'}</button>
        <button type="button" onClick={cancel} style={{ backgroundColor: '#ef4444', color: 'white' }}>
          Cancelar
        </button>
      </div>
    </form>
  );
};

export default TaskForm;
