import React from 'react';
import { Task } from '../types';
import { motion } from 'framer-motion';

interface TaskItemProps {
  task: Task;
  onDelete: (id: string) => void;
  onUpdate: (task: Task) => void;
}

const TaskItem: React.FC<TaskItemProps> = ({ task, onDelete, onUpdate }) => {
  const handleDelete = () => onDelete(task.id);
  const handleUpdate = () => onUpdate(task);

  return (
    <motion.div
      className="task-item"
      initial={{ opacity: 0, y: 10 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, scale: 0.9 }}
      transition={{ duration: 0.3 }}
    >
      <h3>{task.title}</h3>
      <p>{task.description}</p>
      <p>ðŸ“… Fecha: {new Date(task.dueDate).toLocaleDateString()}</p>
      <p>âš¡ Prioridad: {task.priority}</p>
      <p>âœ… Estado: {task.status}</p>
      <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
        <button onClick={handleUpdate}>Actualizar</button>
        <button onClick={handleDelete}>Eliminar</button>
      </div>
    </motion.div>
  );
};

export default TaskItem;
