import React, { useState, useEffect } from 'react';
import TaskForm from './components/TaskForm';
import TaskList from './components/TaskList';
import './styles/App.css';
import { Task } from 'types';

const App: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [formTask, setFormTask] = useState<Task | null>(null);

  const API_URL = 'http://localhost:4000/api/tasks';

  const fetchTasks = async () => {
    const response = await fetch(API_URL);
    const data = await response.json();
    setTasks(data);
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const addTask = async (task: Task) => {
    const response = await fetch(API_URL, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(task),
    });
    const newTask = await response.json();
    setTasks([...tasks, newTask]);
    setFormTask(null);
  };

  const updateTask = async (updatedTask: Task) => {
    await fetch(`${API_URL}/${updatedTask.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedTask),
    });
    setTasks(tasks.map(t => (t.id === updatedTask.id ? updatedTask : t)));
    setFormTask(null);
  };

  const deleteTask = async (id: string) => {
    await fetch(`${API_URL}/${id}`, { method: 'DELETE' });
    setTasks(tasks.filter(t => t.id !== id));
    if (formTask?.id === id) setFormTask(null);
  };

  const handleCreateNew = () => setFormTask(null);
  const handleEdit = (task: Task) => setFormTask(task);

  return (
    <div className="App">
      <button className="toggle-form-btn" onClick={handleCreateNew}>
        {formTask ? '✖ Cancelar' : 'Nueva tarea'}
      </button>

      <TaskForm
        addTask={addTask}
        updateTask={updateTask}
        task={formTask}
        cancel={() => setFormTask(null)}
      />

      <TaskList
        tasks={tasks}
        onDelete={deleteTask}
        onUpdate={updateTask}
        onEdit={handleEdit}
      />
    </div>
  );
};

export default App;
